ChartStock:
query ChartStock($symbol: String!) {
  chartStock(symbol: $symbol) {
    date
    close
    label
  }
}

GetListStock:
query GetListStock {
  getListStock {
    changePercent
    change
    companyName
    symbol
    latestPrice
  }
}

GetStockBySymbol:
query GetStockBySymbol($symbol: String!) {
  getStockBySymbol(symbol: $symbol) {
    symbol
    companyName
    change
    changePercent
    latestPrice
  }
}
