ChartStock:
query ChartStock($symbol: String!, $date: String!) {
  chartStock(symbol: $symbol, date: $date) {
    date
    minute
    label
    close
    average
    marketClose
  }
}

GetListStock:
query GetListStock {
  getListStock {
    changePercent
    change
    companyName
    symbol
    latestPrice
  }
}

GetStockBySymbol:
query GetStockBySymbol($symbol: String!) {
  getStockBySymbol(symbol: $symbol) {
    symbol
    companyName
    change
    changePercent
    latestPrice
  }
}
